{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyPiEOK0dDixUZSjqExEdbGn"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"y0NRSAomaOYL"},"outputs":[],"source":["import os\n","\n","from os.path import join, dirname, realpath\n","from flask import Flask, request, redirect, url_for, send_from_directory, render_template, flash\n","from werkzeug.utils import secure_filename\n","\n","from pipeline import pipeline\n","\n","UPLOAD_FOLDER = join(dirname(realpath(__file__)), 'static/uploads/')\n","ALLOWED_EXTENSIONS = {'png', 'PNG', 'jpg', 'JPG', 'jpeg', 'JPEG', 'gif', 'GIF'}\n","\n","app = Flask(__name__)\n","app.config['UPLOAD_FOLDER'] = UPLOAD_FOLDER\n","app.config['MAX_CONTENT_LENGTH'] = 10 * 1024 * 1024\n","app.secret_key = 'key'\n","\n","\n","def allowed_file(filename):\n","    return '.' in filename and filename.rsplit('.', 1)[1] in ALLOWED_EXTENSIONS\n","\n","\n","@app.route('/')\n","def home():\n","    return render_template('index.html', result=None)\n","\n","\n","# @app.route('/<a>')\n","# def assess():\n","#   return render_template('index.html', result=None, scroll='third')\n","\n","\n","@app.route('/assessment', methods=['GET', 'POST'])\n","def upload_and_classify():\n","    if request.method == 'POST':\n","        if 'file' not in request.files:\n","            flash('No file part')\n","            return redirect(url_for('assess'))\n","\n","        file = request.files['file']\n","\n","        if file.filename == '':\n","            flash('No selected file')\n","            return redirect(url_for('assess'))\n","\n","        if file and allowed_file(file.filename):\n","            filename = secure_filename(file.filename)\n","            file.save(os.path.join(app.config['UPLOAD_FOLDER'], filename))\n","            filepath = os.path.join(app.config['UPLOAD_FOLDER'], filename)\n","            model_results = pipeline.pipe(filepath)\n","\n","            return render_template('results.html', result=model_results, scroll='third', filename=filename)\n","\n","    flash('Invalid file format. Please try again.')\n","    return redirect(url_for('assess'))\n","\n","\n","@app.route('/uploads/<filename>')\n","def send_file(filename):\n","    return send_from_directory(UPLOAD_FOLDER, filename)\n","\n","\n","@app.route('/uploads/<filename>')\n","def uploaded_file(filename):\n","    return send_from_directory(app.config['UPLOAD_FOLDER'], filename)\n","\n","\n","if __name__ == '__main__':\n","    app.run(debug=True, use_reloader=False,)\n"]}]}